import { createSlice } from '@reduxjs/toolkit';


const variValue = {
    toggleValue: true  
    }


const toggleLogic = createSlice({

    name: 'toggle',
    initialState:{
    value: variValue.toggleValue   
    },

    reducers:{
    changeToggle: (state) => {
    state.value = !state.value
    },
    }

})



export const { changeToggle } = toggleLogic.actions

export default toggleLogic.reducer
