import { useState, useEffect, useCallback } from "react";
import style from './DatiFetch.module.css'

const DatiFetch = () => {

const [stateCocktail, changeStateCocktail] =  useState()

const fetchCocktail = async() => {

const response = await fetch('https://thecocktaildb.com/api/json/v1/1/filter.php?c=Cocktail')
if(!response.ok){
//..  
console.log('error') 
}else{
const data = await response.json()
changeStateCocktail(data.drinks)
// console.log(data.results)
//console.log(data.drinks[0].strDrinkThumb)
}}


//  useEffect(() =>{
//     fetchCocktail()
//     },[fetchCocktail])



// const fetchCocktail = useCallback(async () => {
// },[])  

const para = <p style={{'textAlign': 'center', 'width': '100%'}}>Nessun dato presente</p>

return(
<div className={style.container}>
<h2>CARICAMENTO e FILTRO DATI con http request - useEffect e useState</h2>
<div style={{'width':'100%'}}><button onClick={fetchCocktail}>Carica cocktail list</button> </div> 

{stateCocktail=== undefined ? para : stateCocktail.map( cocktail =>
<div key={cocktail.idDrink} className={style.containerCard}>
<div className={style.card}>
<h3>{cocktail.strDrink}</h3> 
<div>
<div>
<img src={cocktail.strDrinkThumb} alt="cocktail"></img> 
</div>
<p><strong>Preparazione:<br></br></strong>Descrizione</p> 
</div>    
</div>
</div>
)}    
</div>
)};

export default DatiFetch;
