import { useState } from 'react';

import style from './Container.module.css'
import Batigol from '../img/batigol.jpg';
import Card from './Card'

const Container = () => {

const [stateDescription, changeStateDescription]  = useState()

const showDetail = (para) => {
const filterPlayer =  giocatori.filter(giocatore => giocatore.nome === para.nome)
changeStateDescription(filterPlayer)
}

const giocatori = [
{nome: 'Francesco',
cognome: 'Totti',
img: 'https://statics.cedscdn.it/photos/ARGOMENTO/MED/132/132_francesco-totti.jpg',
descrizione: 'Francesco è stato il capitano della Roma, considerato il campione assoluto.'
},
{nome: 'Gabriel Omar',
cognome: 'Batistuta',
img: Batigol,
descrizione: 'Batistuta, detto anche il re leone è stato il bomber dello scudetto del 2001.'
},
{nome: 'Marco',
cognome: 'Delvecchio',
img: 'https://www.ilromanista.eu/download/img/full/136241_6zw53bj.jpg',
descrizione: 'Marco è stato il miglior marcatore nei derby di tutti i tempi'
},  
]

return(
<>
<h2>PROVE CREAZIONE COMPONENTI - PROPS ricezione e PROPS evento</h2>
<div className={style.container}>

{ giocatori.map(giocatore =>
 <Card
 nome = {giocatore.nome}
 cognome = {giocatore.cognome}
 img = {giocatore.img}
 descrizione = {giocatore.descrizione}
 showDetail = {showDetail}
 ></Card>   
)
}

{ stateDescription.map( stato =>
 <p>{stato.descrizione}</p>
)

}
</div>
</>
)};

export default Container
