import { useState } from "react"
import FormContact from "./FormContact"
import style from './HomeForm.module.css'
import Profili from "./Profili"

const HomeForm = () => {

const [viewForm, stateViewForm] = useState(false)
const [datiForm, changeDatiForm] = useState([])


const changePage = (para) => {
stateViewForm(para)
}

const updateForm = (para) => {
changeDatiForm([...datiForm, para])
stateViewForm(true)
console.log(datiForm)  
}


return(
<>
<div style={{'marginTop': '30px'}}>
<h2>FORM <br></br> con validazione dati, conferma ed aggiunta nella sezione profili</h2>
{ viewForm ?
<button onClick={() => changePage(false)}
style={{'marginRight': '10px'}} className={style.btnForm}>Compila il Form</button>
: <button onClick={() => changePage(true)}
className={style.btnForm}>Visualizza Profili registrati</button>
}
</div>
{ !viewForm ?
<FormContact
updateForm = {updateForm}
></FormContact>
: <Profili
datiForm = {datiForm}
></Profili>
}
</>
)}

export default HomeForm;
