import { createSlice } from '@reduxjs/toolkit';

const counterLogic = createSlice({

    name: 'counter',
    initialState:{
    value: 0    
    },

    reducers:{

        increment: (state) => {
        state.value += 1    
        },

        decrement: (state) => {
        state.value -= 1    
        },

        incrementByPayload: (state, action) => {
        state.value += action.payload    
        }

    }

});

// export const {increment, decrement, incrementByPayload} = counterLogic.actions

export default counterLogic
